import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../CSS/Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };


  const handleSubmit = (e) => {
    e.preventDefault();

    const basicAuth = btoa(`${email}:${password}`);
    const url = "http://localhost:8085/doubt-sharing-app/auth/user/signIn";

    fetch(url, {
      method: "GET", // Change to POST
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Basic ${basicAuth}`, // Include Basic Auth header
      }
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response data as needed
        console.log(data);
        
        // Assuming the token is in the "Authorization" header
        const token = response.headers.get("Authorization");

        // Now you can use the token as needed (e.g., store it in state or local storage)
        console.log("Token:", token);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={handleEmailChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      {/* <br /> */}
      <Link to="/signup">Sign Up</Link>
    </div>
  );
};

export default Login;
