import React, { useState } from "react";
import "../CSS/Signup.css";
const StudentSignup = () => {
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userLanguage, setUserLanguage] = useState("");
  const [classGrade, setClassGrade] = useState("");

  const handleUserNameChange = (e) => {
    setUserName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleUserLanguageChange = (e) => {
    setUserLanguage(e.target.value);
  };

  const handleClassGradeChange = (e) => {
    setClassGrade(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const studentData = {
      userName,
      email,
      password,
      userLanguage,
      classGrade,
    };
    console.log("Student Signup Data:", studentData);
  };

  return (
    <div>
      <h1>Student Signup</h1>
      <form onSubmit={handleSubmit}>
        <label>
          User Name:
          <input
            type="text"
            value={userName}
            onChange={handleUserNameChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={handleEmailChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </label>
        <br />
        <label>
          User Language:
          <input
            type="text"
            value={userLanguage}
            onChange={handleUserLanguageChange}
            required
          />
        </label>
        <br />
        <label>
          Class Grade:
          <input
            type="text"
            value={classGrade}
            onChange={handleClassGradeChange}
            required
          />
        </label>
        <br />
        <button id="subBtn" type="submit">
          Sign Up
        </button>
        <br />
      </form>
    </div>
  );
};

export default StudentSignup;
