// TutorSignup.jsx
import React, { useState } from "react";
import "../CSS/Signup.css";

const TutorSignup = () => {
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [userLanguage, setUserLanguage] = useState("");
  const [minGrade, setMinGrade] = useState("");
  const [maxGrade, setMaxGrade] = useState("");
  const [subjectExpertise, setSubjectExpertise] = useState("");

  const handleUserNameChange = (e) => {
    setUserName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleUserLanguageChange = (e) => {
    setUserLanguage(e.target.value);
  };

  const handleMinGradeChange = (e) => {
    setMinGrade(e.target.value);
  };

  const handleMaxGradeChange = (e) => {
    setMaxGrade(e.target.value);
  };

  const handleSubjectExpertiseChange = (e) => {
    setSubjectExpertise(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const tutorData = {
      userName,
      email,
      password,
      userLanguage,
      minGrade,
      maxGrade,
      subjectExpertise,
    };
    console.log("Tutor Signup Data:", tutorData);
  };

  return (
    <div>
      <h1>Tutor Signup</h1>
      <form onSubmit={handleSubmit}>
        <label>
          User Name:
          <input
            type="text"
            value={userName}
            onChange={handleUserNameChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={handleEmailChange}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </label>
        <br />
        <label>
          User Language:
          <input
            type="text"
            value={userLanguage}
            onChange={handleUserLanguageChange}
            required
          />
        </label>
        <br />
        <label>
          Minimum Grade:
          <input
            type="text"
            value={minGrade}
            onChange={handleMinGradeChange}
            required
          />
        </label>
        <br />
        <label>
          Maximum Grade:
          <input
            type="text"
            value={maxGrade}
            onChange={handleMaxGradeChange}
            required
          />
        </label>
        <br />
        <label>
          Subject Expertise:
          <input
            type="text"
            value={subjectExpertise}
            onChange={handleSubjectExpertiseChange}
            required
          />
        </label>
        <br />
        <button id="subBtn" type="submit">Sign Up</button>
        <br />
      </form>
    </div>
  );
};

export default TutorSignup;
